DROP PROCEDURE IF EXISTS `cursor_test`;
delimiter ;;
CREATE PROCEDURE `cursor_test`()
BEGIN  
    declare c int;  
    declare n varchar(20);  
    declare total int default 0;  
    declare done int default false;  
    declare cur cursor for select title,source from tb_article where title like '%蔬菜%';  
    declare continue HANDLER for not found set done = true;  
    set total = 0;  
    open cur;  
    fetch cur into n,c;  
    while(not done) do  
        set total = total + c;  
        fetch cur into n,c;  
    end while;  
      
    close cur;  
    select total;  
END
;;
delimiter ;

